from pybricks.hubs import PrimeHub
from pybricks.parameters import Direction, Port
from pybricks.pupdevices import Motor
from pybricks.robotics import DriveBase

# Set up all devices.
prime_hub = PrimeHub()
right_motor = Motor(Port.A, Direction.COUNTERCLOCKWISE)
left_motor = Motor(Port.B, Direction.CLOCKWISE)
front_motor = Motor(Port.E, Direction.CLOCKWISE)
top_motor = Motor(Port.F, Direction.COUNTERCLOCKWISE)
drive_base = DriveBase(left_motor, right_motor, 81.6, 114)


# The main program starts here.
print('Hello Dakiwi!')
drive_base.curve(-100, -33)
drive_base.straight(-250)
drive_base.curve(-350, 80)
drive_base.curve(-500, -50)
drive_base.straight(380)
drive_base.curve(350, -60)
drive_base.curve(-250, 70)
