from pybricks.hubs import PrimeHub
from pybricks.parameters import Direction, Port, Stop
from pybricks.pupdevices import Motor
from pybricks.robotics import DriveBase

# Set up all devices.
prime_hub = PrimeHub()
motor = Motor(Port.A, Direction.CLOCKWISE)
motor_2 = Motor(Port.B, Direction.COUNTERCLOCKWISE)
front = Motor(Port.E, Direction.COUNTERCLOCKWISE)
back = Motor(Port.F, Direction.CLOCKWISE)
drive_base = DriveBase(motor_2, motor, 81.6, 101.7182)


# The main program starts here.
# pink attachment holder on 3rd black line
drive_base.curve(-100, 17)
drive_base.straight(-600)
drive_base.settings(straight_speed=50)
drive_base.turn(85)
drive_base.straight(-110)
back.run_angle(888, -940)
drive_base.straight(175)
drive_base.settings(straight_speed=200)
drive_base.turn(95)
front.run_angle(500, 320)
drive_base.straight(345)
drive_base.turn(-15)
front.run_angle(500, -550)
