from pybricks.parameters import Direction, Port, Stop
from pybricks.pupdevices import Motor
from pybricks.robotics import DriveBase
from pybricks.tools import multitask, run_task, wait

# Set up all devices.
RIGHT = Motor(Port.A, Direction.CLOCKWISE)
LEFT = Motor(Port.B, Direction.COUNTERCLOCKWISE)
FRONT = Motor(Port.C, Direction.CLOCKWISE)
motor = Motor(Port.D, Direction.CLOCKWISE)
drive_base = DriveBase(LEFT, RIGHT, 81.6, 110)

async def main():
    drive_base.settings(straight_speed=400)
    await drive_base.straight(190)
    drive_base.settings(straight_speed=170)
    await multitask(FRONT.run_angle(300, -270, Stop.NONE))
    await drive_base.straight(280, Stop.NONE)


run_task(main())