from pybricks.parameters import Direction, Port, Stop
from pybricks.pupdevices import Motor
from pybricks.robotics import DriveBase
from pybricks.tools import multitask, run_task, wait

# Set up all devices.
RIGHT = Motor(Port.A, Direction.CLOCKWISE)
LEFT = Motor(Port.B, Direction.COUNTERCLOCKWISE)
FRONT = Motor(Port.E, Direction.COUNTERCLOCKWISE)
drive_base = DriveBase(LEFT, RIGHT, 80, 114)

async def subtask():
    drive_base.settings(straight_speed=200)
    await drive_base.straight(30, Stop.NONE)

async def main():
    # the left wheel is on the second thin line after the first thick line
    # the right wheel is on the third thin line after the second thick line
    # the back is on the wall
    drive_base.settings(straight_speed=1000)
    await drive_base.straight(20)
    await drive_base.curve(180, -70)
    await drive_base.curve(180, -10)
    await drive_base.straight(10)
    await FRONT.run_angle(1000, -70)
    await multitask(FRONT.run_angle(1000, -360, Stop.NONE), subtask())
    await drive_base.straight(30, Stop.NONE)
    await FRONT.run_angle(1000, -240)
    drive_base.settings(straight_speed=1000)
    await drive_base.curve(-150, 140)
    drive_base.settings(straight_speed=1000)
    await drive_base.straight(600)


run_task(main())