from pybricks.hubs import PrimeHub
from pybricks.parameters import Direction, Port, Stop
from pybricks.pupdevices import Motor
from pybricks.robotics import DriveBase

# Set up all devices.
prime_hub = PrimeHub()
left = Motor(Port.B, Direction.COUNTERCLOCKWISE)
right = Motor(Port.A, Direction.CLOCKWISE)
front = Motor(Port.E, Direction.COUNTERCLOCKWISE)
top = Motor(Port.F, Direction.CLOCKWISE)
drive_base = DriveBase(left, right, 81, 90)


# The main program starts here.
drive_base.use_gyro(True)
front.control.limits(torque=800)
drive_base.settings(straight_speed=450)
drive_base.turn(40)
drive_base.straight(-540)
drive_base.turn(53)
drive_base.settings(straight_speed=250)
drive_base.straight(200, Stop.COAST)
front.run_angle(100, 120)
drive_base.straight(-100, Stop.COAST)
drive_base.settings(straight_speed=550)
drive_base.turn(-75)
drive_base.straight(550, Stop.BRAKE)
