from pybricks.hubs import PrimeHub
from pybricks.parameters import Direction, Port
from pybricks.pupdevices import Motor
from pybricks.robotics import DriveBase

# Set up all devices.
prime_hub = PrimeHub()
left = Motor(Port.A, Direction.COUNTERCLOCKWISE)
right = Motor(Port.B, Direction.CLOCKWISE)
front = Motor(Port.E, Direction.CLOCKWISE)
top = Motor(Port.F, Direction.COUNTERCLOCKWISE)
drive_base = DriveBase(left, right, 81, 100)


# The main program starts here.
drive_base.use_gyro(True)
prime_hub.imu.reset_heading(0)
# liine up the front motor side
drive_base.settings(straight_speed=550)
drive_base.straight(-200)
print(drive_base.distance())
drive_base.turn(90)
print(prime_hub.imu.heading())
drive_base.straight(-200)
print(drive_base.distance())
drive_base.turn(-90)
print(prime_hub.imu.heading())
drive_base.straight(200)
print(drive_base.distance())
