from pybricks.hubs import PrimeHub
from pybricks.parameters import Direction, Port, Stop
from pybricks.pupdevices import Motor
from pybricks.robotics import DriveBase
from pybricks.tools import multitask, run_task, wait

# Set up all devices.
prime_hub = PrimeHub()
left = Motor(Port.A, Direction.COUNTERCLOCKWISE)
right = Motor(Port.B, Direction.CLOCKWISE)
front = Motor(Port.E, Direction.CLOCKWISE)
top = Motor(Port.F, Direction.COUNTERCLOCKWISE)
drive_base_2 = DriveBase(left, right, 81, 99)

# Initialize variables.
heading = 0

async def main():
    global heading
    drive_base_2.use_gyro(True)
    front.control.limits(torque=450)
    drive_base_2.settings(straight_speed=550)
    await multitask(front.run_angle(200, 92), drive_base_2.turn(30))
    await drive_base_2.straight(-350)
    await drive_base_2.turn(-30)
    heading = prime_hub.imu.heading()
    print('After two curve turns, the heading is: ', heading)
    await drive_base_2.straight(-410)
    await front.run_angle(500, 65)
    await front.run_angle(500, -65)
    await drive_base_2.straight(100)
    await front.run_angle(500, -95)
    await front.run_angle(500, 95)


run_task(main())