from pybricks.parameters import Direction, Port, Stop
from pybricks.pupdevices import Motor
from pybricks.robotics import DriveBase
from pybricks.tools import multitask, run_task, wait

# Set up all devices.
right = Motor(Port.A, Direction.CLOCKWISE)
left = Motor(Port.B, Direction.COUNTERCLOCKWISE)
front = Motor(Port.E, Direction.CLOCKWISE)
drive_base = DriveBase(left, right, 81.6, 114)

async def main():
    # The intial position is the back is on the thick egg and the wheel secure
    # bar is on the 3rd thick line going vertical (from the back again :-)
    print('Hello, Pybricks!')
    drive_base.use_gyro(True)
    await multitask(front.run_angle(40, -60), drive_base.straight(550))
    front.control.limits(acceleration=3000)
    # Instead of 60 degree, here we use 50 degree so that
    await front.run_angle(150, 50)
    await drive_base.straight(200)
    # 400 degree/seconds is important for the shark delivery
    await front.run_angle(500, -120)
    # This backup is to allow the bar to
    await drive_base.straight(-130)
    await front.run_angle(200, 120)
    await drive_base.straight(300)
    await front.run_angle(500, -60)
    drive_base.settings(straight_speed=500)
    await drive_base.curve(-90, 180)
    await drive_base.straight(-180)
    await drive_base.straight(90)
    await drive_base.curve(140, 180)
    await drive_base.straight(800)


run_task(main())