from pybricks.hubs import PrimeHub
from pybricks.parameters import Direction, Port, Stop
from pybricks.pupdevices import Motor
from pybricks.robotics import DriveBase
from pybricks.tools import multitask, run_task, wait

# Set up all devices.
prime_hub = PrimeHub()
motor = Motor(Port.A, Direction.CLOCKWISE)
motor2 = Motor(Port.B, Direction.COUNTERCLOCKWISE)
front = Motor(Port.E, Direction.COUNTERCLOCKWISE)
back = Motor(Port.F, Direction.CLOCKWISE)
drive_base = DriveBase(motor2, motor, 81.6, 101.7182)

async def main():
    drive_base.use_gyro(True)
    await drive_base.turn(10)
    drive_base.settings(straight_speed=350)
    await drive_base.straight(-300)
    drive_base.settings(straight_speed=200)
    await drive_base.straight(-100)
    # octopie collected
    drive_base.reset()
    await multitask(front.run_angle(500, 45), drive_base.straight(30))
    await drive_base.curve(90, -95)
    drive_base.reset()
    await drive_base.straight(95)
    await front.run_angle(250, -370)
    # shippy lane done
    drive_base.settings(straight_speed=700)
    drive_base.settings(turn_rate=150)
    await drive_base.straight(-250)
    await drive_base.turn(74)
    await drive_base.straight(400)


run_task(main())