from pybricks.parameters import Direction, Port, Stop
from pybricks.pupdevices import Motor
from pybricks.robotics import DriveBase
from pybricks.tools import multitask, run_task, wait

# Set up all devices.
right = Motor(Port.A, Direction.CLOCKWISE)
left = Motor(Port.B, Direction.COUNTERCLOCKWISE)
front = Motor(Port.E, Direction.CLOCKWISE)
back = Motor(Port.F, Direction.CLOCKWISE)
drive_base = DriveBase(left, right, 81.6, 110)

async def main():
    print('Hello, Pybricks!')
    drive_base.use_gyro(True)
    drive_base.settings(straight_speed=100)
    await drive_base.straight(-50)
    await front.run_angle(40, -47)
    drive_base.settings(straight_speed=500)
    await drive_base.curve(102, 180)
    await drive_base.straight(-30)
    await drive_base.straight(-10)
    await front.run_angle(150, 50)
    await drive_base.straight(287)
    await front.run_angle(500, -120)
    await drive_base.straight(-20)
    await front.run_angle(200, 120)
    await drive_base.straight(215)
    await front.run_angle(500, -60)
    drive_base.settings(straight_speed=500)
    await drive_base.curve(-87.45, 179.75)
    await wait(400)
    # me change used to be 250
    await drive_base.straight(-217.85)
    await wait(1000)
    await back.run_angle(200, -195)
    await wait(1000)
    drive_base.settings(straight_speed=1000)
    await drive_base.straight(100)
    await drive_base.curve(155, 180)
    await drive_base.straight(550, Stop.NONE)
    await drive_base.curve(250, 60)


run_task(main())