from pybricks.parameters import Direction, Port, Stop
from pybricks.pupdevices import Motor
from pybricks.robotics import DriveBase
from pybricks.tools import wait

# Set up all devices.
right = Motor(Port.A, Direction.CLOCKWISE)
left = Motor(Port.B, Direction.COUNTERCLOCKWISE)
front = Motor(Port.E, Direction.CLOCKWISE)
back = Motor(Port.F, Direction.CLOCKWISE)
drive_base = DriveBase(left, right, 81.6, 110)


# The main program starts here.
print('Hello, Pybricks!')
drive_base.use_gyro(True)
drive_base.settings(straight_speed=100)
drive_base.settings(straight_speed=500)
drive_base.curve(170, 45)
drive_base.curve(170, -45)
drive_base.straight(520)
front.run_angle(3000, -150)
front.run_angle(800, 30)
drive_base.straight(108)
front.run_angle(3000, 125)
wait(500)
front.run_angle(300, -45)
drive_base.straight(-830)
