from pybricks.parameters import Direction, Port
from pybricks.pupdevices import Motor
from pybricks.robotics import DriveBase

# Set up all devices.
motor = Motor(Port.A, Direction.CLOCKWISE)
motor_2 = Motor(Port.B, Direction.COUNTERCLOCKWISE)
drive_base_2 = DriveBase(motor_2, motor, 52, 82.55)


# The main program starts here.
# Inatial  position is 14th square for the left wheel.
print('Dakiwi is AWESOME!!!')
drive_base_2.use_gyro(True)
drive_base_2.straight(590)
drive_base_2.turn(-90)
drive_base_2.settings(straight_speed=500)
drive_base_2.straight(110)
drive_base_2.straight(-90)
drive_base_2.settings(straight_speed=500)
drive_base_2.turn(55)
drive_base_2.straight(200)
drive_base_2.straight(-100)
drive_base_2.turn(30)
drive_base_2.straight(100)
