from pybricks.parameters import Direction, Port, Stop
from pybricks.pupdevices import Motor
from pybricks.robotics import DriveBase

# Set up all devices.
motor = Motor(Port.A, Direction.CLOCKWISE)
motor_2 = Motor(Port.B, Direction.COUNTERCLOCKWISE)
motor_3 = Motor(Port.F, Direction.CLOCKWISE)
drive_base = DriveBase(motor_2, motor, 80, 114)


# The main program starts here.
print('Hello, Pybricks!')
drive_base.straight(-290)
drive_base.turn(-30)
drive_base.straight(-88)
motor.control.limits(acceleration=2000)
motor_2.control.limits(acceleration=2000)
drive_base.turn(44)
drive_base.straight(-80)
motor_3.run_angle(500, -360)
