from pybricks.hubs import PrimeHub
from pybricks.parameters import Direction, Port, Stop
from pybricks.pupdevices import Motor
from pybricks.robotics import DriveBase

# Set up all devices.
prime_hub = PrimeHub()
right_motor = Motor(Port.B, Direction.CLOCKWISE)
left_motor = Motor(Port.A, Direction.COUNTERCLOCKWISE)
top = Motor(Port.F, Direction.CLOCKWISE)
front = Motor(Port.E, Direction.COUNTERCLOCKWISE)
drive_base = DriveBase(left_motor, right_motor, 56, 114)


# The main program starts here.
drive_base.use_gyro(True)
drive_base.straight(165)
drive_base.turn(90)
drive_base.straight(75)
top.run_angle(500, 975)
top.run_angle(500, -1900)
top.run_angle(500, 1025)
drive_base.straight(-75)
drive_base.turn(90)
drive_base.straight(165)
