from pybricks.parameters import Direction, Port
from pybricks.pupdevices import Motor
from pybricks.robotics import DriveBase

# Set up all devices.
right = Motor(Port.A, Direction.CLOCKWISE)
left = Motor(Port.B, Direction.COUNTERCLOCKWISE)
drive_base = DriveBase(left, right, 81.4, 101.7182)


# The main program starts here.
drive_base.use_gyro(True)
drive_base.settings(straight_speed=80)
drive_base.straight(150)
drive_base.straight(-150)
# finish push out segments
drive_base.settings(straight_speed=350)
drive_base.turn(-25)
drive_base.straight(605)
drive_base.turn(71)
drive_base.settings(straight_speed=90)
drive_base.straight(330)
drive_base.settignss(straight_speed=150)
drive_base.straight(-330)
